document.onkeypress=getPrintableKey,document.onkeydown=getUniKey;const terminal=document.getElementById("terminal");let lastLine=terminal.lastElementChild,input="";function getPrintableKey(a){"Enter"===a.key||(input+=a.key,updateScreen())}function getUniKey(a){parseKey(a.key)}function updateScreen(){lastLine.innerHTML=input+"<span></span>",updateScroll()}function updateScroll(){terminal.scrollTop=terminal.scrollHeight}function parseKey(a){switch(a){case"Enter":const b=input.split(" ");parseCmd(...b),input="";break;case"Backspace":input=input.slice(0,input.length-1),updateScreen();break;case"ArrowUp":break;default:}updateScreen()}function parseCmd(a,...b){"help"===a?help(...b):"ls"===a?ls(...b):"cat"===a?cat(...b):""===a?println(""):output(`Unknow command ${a}`)}function println(a,b=!1){lastLine.innerHTML=lastLine.innerHTML.replace("<span></span>","");let c=document.createElement("div");c.innerHTML=a,b&&(c.className="output"),terminal.appendChild(c),lastLine=c}function output(a){println(a,!0),println("")}function help(){output(`
  <div class="help">
    <div>ls</div><div>list directory contents</div>
    <div>cat</div><div>concatenate and print files</div>
  </div>
`)}function cat(...a){switch(a[0]){case"profile":output("name: Panot Wongkhot");break;case"contact":output("panot.wongkhot@gmail.com");break;default:output("file not found.");}}function ls(...a){output("profile contact projects")}